import React, { useState, useEffect } from 'react';
// import { useHistory } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';

const StyledMovieListCont = styled.div`
    width: 80%;
    padding: 0 15px;
    margin: 0 auto;
`

const StyledMovieList = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
`;

const StyledMovieCard = styled.div`
    flex-basis: calc(33.33% - 10px);
    margin-bottom: 1rem;
    // width: 33.3333333333333%;
    // padding: 0 15px;
    // margin: 15px 0;
`;

const StyledMovieTitleDate = styled.div`
    margin-bottom: 17px;
`

const MovieList = () => {
    const [movies, setMovies] = useState([]);
    // const history = useHistory();

    useEffect(() => {
        axios.get('https://swapi.dev/api/films')
            .then(response => {
                setMovies(response.data.results);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);

    // const handleMovieClick = (url) => {
    //     // Navigate to a new page that displays more information about the movie
    //     history.push(`/movie/${url}`);
    // }

    return (
        <StyledMovieListCont>
            <StyledMovieList>
                {movies.map(movie => (
                    <StyledMovieCard key={movie.episode_id}>
                        <StyledMovieTitleDate>
                            <h3>
                                <a href={movie.url}>
                                    {movie.title}
                                </a>
                            </h3>
                            <p>{movie.release_date}</p>
                        </StyledMovieTitleDate>

                        <p>{movie.opening_crawl}</p>
                        <button>More info</button>
                    </StyledMovieCard>
                ))}
            </StyledMovieList>
        </StyledMovieListCont>
    );
};

