import React, { useState, useEffect } from 'react';
// import { useHistory } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';

const StyledMovieListCont = styled.div`
    width: 80%;
    padding: 0 15px;
    margin: 0 auto;
`

const StyledMovieList = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
`;

const StyledMovieCard = styled.div`
    flex-basis: calc(33.33% - 10px);
    margin-bottom: 1rem;
`;

const StyledMovieTitleDate = styled.div`
    margin-bottom: 17px;
`;

const StyledMovieTitle = styled.h3`
    font-family: "Star Jedi", "Open Sans", sans-serif;
    font-size: 22px;
    font-weight: 700;
    letter-spacing: 2px;
`

const StyledMovieTitle2 = styled.a`
    color: rgb(255, 255, 255);
    text-decoration: none;
`

const StyledMovieDate = styled.span`
    color: rgb(157, 157, 157);
    font-size: 14px;
`
const StyledMovieInfo = styled.p`
    font-family: inherit;
    font-size: 14px;
    color: rgb(255, 255, 255);
    overflow-wrap: break-word;
    white-space: normal;
    margin-bottom: 2.5rem;
`



const MovieList = () => {
    const [movies, setMovies] = useState([]);
    // const history = useHistory();

    useEffect(() => {
        axios.get('https://swapi.dev/api/films')
            .then(response => {
                setMovies(response.data.results);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);

    // const handleMovieClick = (url) => {
    //     // Navigate to a new page that displays more information about the movie
    //     history.push(`/movie/${url}`);
    // }

    return (
        <StyledMovieListCont>
            <StyledMovieList>
                {movies.map(movie => (
                    <StyledMovieCard key={movie.episode_id}>
                        <StyledMovieTitleDate>
                            <StyledMovieTitle>
                                <StyledMovieTitle2 href={movie.url}>
                                    {movie.title}
                                </StyledMovieTitle2>
                            </StyledMovieTitle>
                            <StyledMovieDate>{movie.release_date}</StyledMovieDate>
                        </StyledMovieTitleDate>

                        <StyledMovieInfo>{movie.opening_crawl}</StyledMovieInfo>
                        <p>
                            <a href={movie.url}>More info</a>
                        </p>
                    </StyledMovieCard>
                ))}
            </StyledMovieList>
        </StyledMovieListCont>
    );
};

