import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import bgCard1 from '../images/bg';
import bgCard2 from '../images/bg-card-2.b0a7b209.png';
import bgCard3 from '../images/bg-card-3.1914d853.png';
import bgCard4 from '../images/bg-card-4.c21c4d4b.png';
import bgCard5 from '../images/bg-card-5.67d061d6.png';
import bgCard6 from '../images/bg-card-6.b4fea25d.png';


const StyledMovieListCont = styled.div`
    width: 80%;
    padding: 0 15px;
    margin: 0 auto;
`

const StyledMovieList = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
`;

const StyledMovieCardCont = styled.div`
    flex-basis: calc(33.33% - 10px);
    padding: 0 15px;
    margin: 15px 0;
`;

const StyledMovieCard = styled.div`
    display: block;
    background-color: rgb(35, 35, 35);
    background-image: url(${props => episodeBackgrounds[props.episodeId]});
    // background-image: url(${props => props.backgroundImage});
    padding: 20px;
    color: rgb(255, 255, 255);
    transform: perspective(1px) translateZ(0px);
    box-shadow: transparent 0px 0px 10px 3px, rgb(0, 0, 0) 0px 0px 10px;
    transition: all 0.3s ease 0s;
    overflow: hidden;
    border-radius: 0.5rem;

    &:hover {
        box-shadow: rgb(32, 152, 209) 0px 0px 10px 3px, rgb(0, 0, 0) 0px 0px 10px;
    }   
`;

// const StyledMovieCard = styled.div`
//     display: block;
//     background-color: rgb(35, 35, 35);
//     padding: 20px;
//     color: rgb(255, 255, 255);
//     transform: perspective(1px) translateZ(0px);
//     box-shadow: transparent 0px 0px 10px 3px, rgb(0, 0, 0) 0px 0px 10px;
//     transition: all 0.3s ease 0s;
//     overflow: hidden;
//     border-radius: 0.5rem;

//     // background-image: url(${props => `./images/episode-${props.episode}.jpg`});
//     background-repeat: no-repeat;
//     background-size: cover;
//     background-position: center;

//     &:hover {
//         box-shadow: rgb(32, 152, 209) 0px 0px 10px 3px, rgb(0, 0, 0) 0px 0px 10px;
//     }   
// `;

const StyledMovieTitleDate = styled.div`
    display: flex;
    flex-direction: column;
    margin-bottom: 17px;
`;

const StyledMovieTitle = styled.h3`
    font-family: "Star Jedi", "Open Sans", sans-serif;
    font-size: 22px;
    font-weight: 700;
    letter-spacing: 2px;
`;

const StyledMovieTitle2 = styled.a`
    color: rgb(255, 255, 255);
    text-decoration: none;
`;

const StyledMovieDate = styled.span`
    color: rgb(157, 157, 157);
    font-size: 14px;
`;

const StyledMovieInfo = styled.p`
    font-family: inherit;
    font-size: 16px;
    color: rgb(255, 255, 255);
    overflow: hidden;
    // max-height: 7.5em; /* set maximum height equivalent to 7 lines */
    text-overflow: ellipsis;
    display: -webkit-box;
    -webkit-line-clamp: 7; /* specify the number of lines */
    -webkit-box-orient: vertical;
    margin-bottom: 25px;
    word-wrap: break-word;
    // hyphens: auto;
`;

const StyledMovieInfoMoreInfo = styled.p`
    padding-top: 1.2rem;
    font-size: 14px;
    text-transform: capitalize;
    border-top: 2px solid rgb(142, 46, 46);
`;

const StyledMovieInfoMoreInfo2 = styled.a`
    color: rgb(255, 232, 31);
    text-decoration: none;
`;

const episodeBackgrounds = {
    1: bgCard1,
    2: bgCard2,
    3: bgCard3,
    4: bgCard4,
    5: bgCard5,
    6: bgCard6,
};


// const getBackgroundImage = (episodeId) => {
//     const episodeImages = {
//         1: "bg-card-1.0c96fd00.png",
//         2: "bg-card-2.b0a7b209.png",
//         3: "bg-card-3.1914d853.png",
//         4: "bg-card-4.c21c4d4b.png",
//         5: "bg-card-5.67d061d6.png",
//         6: "bg-card-6.b4fea25d.png",
//     };
//     return `./images/${episodeImages[episodeId]}`;
// };



const MovieList = () => {
    const [movies, setMovies] = useState([]);
    // const history = useHistory();

    const formatDate = (dateStr) => {
        const date = new Date(dateStr);
        return date.toLocaleDateString('en-US', { month: 'long', day: 'numeric', year: 'numeric' });
    };

    // const bgImages = [bgCard1, bgCard2, bgCard3, bgCard4, bgCard5, bgCard6];


    useEffect(() => {
        axios.get('https://swapi.dev/api/films')
            .then(response => {
                setMovies(response.data.results);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);

    // const handleMovieClick = (url) => {
    //     // Navigate to a new page that displays more information about the movie
    //     history.push(`/movie/${url}`);
    // }

    return (
        <StyledMovieListCont>
            <StyledMovieList>
                {movies.map(movie => (
                    <StyledMovieCardCont key={movie.episode_id}>
                        <StyledMovieCard episodeId={movie.episode_id}>
                        {/* <StyledMovieCard style={{ backgroundImage: `url(${bgImages[movie.episode_id - 1]})` }} > */}
                            <StyledMovieTitleDate>
                                <StyledMovieTitle>
                                    <StyledMovieTitle2 href={movie.url}>{movie.title}</StyledMovieTitle2>
                                </StyledMovieTitle>
                                <StyledMovieDate>{formatDate(movie.release_date)}</StyledMovieDate>
                            </StyledMovieTitleDate>

                            <StyledMovieInfo>{movie.opening_crawl}</StyledMovieInfo>

                            <StyledMovieInfoMoreInfo>
                                <StyledMovieInfoMoreInfo2 href={movie.url}>More info</StyledMovieInfoMoreInfo2>
                            </StyledMovieInfoMoreInfo>
                        </StyledMovieCard>
                    </StyledMovieCardCont>
                ))}
            </StyledMovieList>
        </StyledMovieListCont>
    );
};

    // return (
    //     <StyledMovieListCont>
    //         <StyledMovieList>
    //             {movies.map(movie => (
    //                 <StyledMovieCard key={movie.episode_id} className="movie-card">
    //                     <a href={movie.url} onClick={() => handleMovieClick(movie.url)}>
    //                         <h2>{movie.title}</h2>
    //                     </a>
    //                     <p>Release Date: {movie.release_date}</p>
    //                     <p>{movie.opening_crawl}</p>
    //                     <button>More info</button>
    //                 </StyledMovieCard >
    //             ))}
    //         </StyledMovieList>
    //     </StyledMovieListCont>
    // );

export default MovieList;






